import { createRouter, createWebHistory } from 'vue-router';
import Home from '../views/HomeView.vue';

const routes = [
  {
    path: '/',
    name: 'Home',
    component: Home,
    alias: [ // Using alias to handle hash navigation within the home page
      '/#about',
      '/#news',
      '/#publications',
      '/#projects',
      '/#education'
    ]
  }
];

const router = createRouter({
  history: createWebHistory(),
  routes,
  scrollBehavior(to, from, savedPosition) {
    // Handle scrolling to specific sections
    if (to.hash) {
      return new Promise((resolve) => {
        // Ensure the page has loaded and the elements are available
        setTimeout(() => {
          resolve({
            el: decodeURIComponent(to.hash), // Target the element, using decodeURIComponent to handle special characters in the hash
            behavior: 'smooth',
            top: 70 // Optional: Top offset if you have a fixed header, etc.
          });
        }, 300); // Set a timeout to ensure that the scroll happens after the page has rendered
      });
    } else if (savedPosition) {
      return savedPosition;
    } else {
      return { top: 0 };
    }
  }
});

export default router;
